// image_available_semaphores 图片可用的信号量  render... 渲染结束的信号量  in... 正在传输的墙(内存屏障)
// 这个实现虽然有点捞但是比较容易看懂
let (image_available_semaphores, render_finished_semaphores, in_flight_fences) = {
    let mut image_available_semaphores: Vec::Semaphore> = vec![];
    let mut render_finished_semaphores: Vec::Semaphore> = vec![];
    let mut in_flight_fences: Vec::Fence> = vec![];
    for _ in 0..frames_in_flight {
        in_flight_fences
            .push(device.create_fence(true).map_err(|_| "创建内存屏障失败")?);
