            .push(device.create_semaphore().map_err(|_| "创建信号量失败")?);
        render_finished_semaphores
            .push(device.create_semaphore().map_err(|_| "创建信号量失败")?);
    }
    (
        image_available_semaphores,
        render_finished_semaphores,
        in_flight_fences,
    )
};

